package com.mycompany.cortijomorenoruben_ficcion;

import java.util.Scanner;

/**
 *
 * @author ruben
 */

/**
 * <h2>Clase Principal</h2>
 * <p>Contiene el método main y demás metodos del programa.</p>
 * <p>Incluye el menú y creación de objetos de ejemplo.</p>
 */

public class CortijoMorenoRuben_Ficcion {
    private static Serie[] series;
    private static Pelicula[] peliculas;
    private static Cliente[] clientes;
    private static Actor[] actores;
    
   public static void main(String[] args) {
        iniciarPrograma();
    }
    
    private static void iniciarPrograma() {
        crearObjetosDeEjemplo();
        mostrarMenu();
    }
    
private static void crearObjetosDeEjemplo() {
    
    peliculas = new Pelicula[3];
    peliculas[0] = new Pelicula("Interestellar", 120, "data:image/jpeg;base64,");
    peliculas[1] = new Pelicula("Coraline", 90, "data:image/jpeg;base64,");
    peliculas[2] = new Pelicula("Fog City", 70, "data:image/jpeg;base64,");
    
    series = new Serie[3];
    series[0] = new Serie("Breaking Bad", 5, new int[]{7, 13, 13, 13, 16}, new int[]{45, 45, 45, 45, 45}, "https://m.media-amazon.com/images/M/MV5BMzU5ZGYzNmQtMTdhYy00OGRiLTg0NmQtYjVjNzliZTg1ZGE4XkEyXkFqcGc@._V1_QL75_UX190_CR0,2,190,281_.jpg");
    series[1] = new Serie("Rick and Morty", 8, new int[]{11, 10, 10, 10, 10, 10, 10, 10}, new int[]{22, 22, 22, 22, 22, 22, 22, 22}, "data:image/jpeg;base64,");
    series[2] = new Serie("Dorohedoro", 1, new int[]{12}, new int[]{24}, "data:image/jpeg;base64,");
    
    clientes = new Cliente[2];
    clientes[0] = new Cliente("Rubén Cortijo", 25, TipoCliente.PREMIUM);
    clientes[1] = new Cliente("Luna Martinez", 30, TipoCliente.BASICO);

    actores = new Actor[2];
    actores[0] = new Actor("Ryan Gosling", 45);
    actores[1] = new Actor("Willem Dafoe", 70);
}
    
    private static void mostrarMenu() {
        Scanner scanner = new Scanner(System.in);
        boolean salir = false;

        while (!salir) {
            System.out.println("Menú:");
            System.out.println("1. Series");
            System.out.println("2. Películas");
            System.out.println("3. Clientes");
            System.out.println("4. Actores");
            System.out.println("5. Salir");
            System.out.print("Opción: ");

            int opcion = scanner.nextInt();

            switch (opcion) {
                case 1:
                    mostrarInformacionSeries();
                    break;
                case 2:
                    mostrarInformacionPeliculas();
                    break;
                case 3:
                    mostrarInformacionClientes();
                    break;
                case 4:
                    mostrarInformacionActores();
                    break;
                case 5:
                    salir = true;
                    System.out.println("Saliendo...");
                    break;
                default:
                    System.out.println("Error");
            }
        }
        scanner.close();
    }
    
    private static void mostrarInformacionSeries() {
        System.out.println("Series: ");
        for (Serie serie : series) {
            System.out.println("------------------------");
            serie.mostrarAtributos();
            serie.mostrarId();
            System.out.println("------------------------\n");
        }
    }
    
    private static void mostrarInformacionPeliculas() {
        System.out.println("Peliculas: ");
        for (Pelicula pelicula : peliculas) {
            System.out.println("------------------------");
            pelicula.mostrarAtributos();
            pelicula.mostrarId();
            System.out.println("------------------------\n");
        }
    }
    
    private static void mostrarInformacionClientes() {
        System.out.println("Clientes: ");
        for (Cliente cliente : clientes) {
            System.out.println("------------------------");
            cliente.mostrarAtributos();
            cliente.mostrarId();
            System.out.println("------------------------\n");
        }
    }
    
    private static void mostrarInformacionActores() {
        System.out.println("Actores: ");
        for (Actor actor : actores) {
            System.out.println("------------------------");
            actor.mostrarAtributos();
            actor.mostrarId();
            System.out.println("------------------------\n");
        }
    }
}

